--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=951 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (5,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','','Hem','','publish','open','open','','hem','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',0,'http://localhost2/?p=5',1,'nav_menu_item','',0),(18,1,'2010-10-11 19:02:59','2010-10-11 17:02:59','<p class=\"lead\">Gizur is a consulting firm specialized in web/mobile and business process development.\r\n\r\nWe have extensive experience from helping large and medium sized companies enhancing their business processes.</p>\r\n\r\n<p>Our areas of expertise are:</p>\r\n<ol>\r\n	<li>Implementation of ERP-systems</li>\r\n	<li>Web and mobile solutions</li>\r\n	<li>Project- and program management</li>\r\n</ol>\r\n\r\n<p>Examples of industry sectors we have been working in are grocery-, convenience- and fashion retailing and also department stores.\r\n\r\nWe also delvier SAP Retail implementations in partnership with <a href=\"http://www.groupsoft.in\">Groupsoft</a> as well as Oracle\'s retail solutions.</p>','','','publish','closed','closed','','home','','','2012-12-03 17:00:05','2012-12-03 16:00:05','',0,'http://www.gizur.com/?page_id=18',2,'page','',0),(28,1,'2010-10-11 20:55:56','2010-10-11 18:55:56','<p class=\"lead\">Vi på Gizur har lång erfarenhet av IT och verksamhetsutveckling inom en rad olika branscher och förstår vikten av att sätta verksamheten i fokus. Kunskapen om branschen i kombination med gediget IT-kunnande är nyckeln till effektiva lösningar.</p>\r\n\r\n<h4>Web och mobilitet</h4>\r\n\r\n<p>Genom att arbeta med web- och mobila tekniker bygger vi framtidssäkra lösningar. Den tillgänglighet som web- och mobila lösningar ger är svårt att uppnå med tidigare tekniker. För att minska ledtiden till leverans och hålla ner kostnaderna väljer många av våra kunder att även använda molntekniker.</p>\r\n\r\n<a href=\"/?page_id=90\">Mer om web och mobilitet</a>\r\n\r\n<h4>Affärssytem</h4>\r\n\r\n<p>I vårt arbete med IT och verksamhetsutveckling hjälper vi våra kunder att effektivisera sina processer. En förmåga att sätta oss in i kundernas affär och kreativa lösningar är det recept som gett oss trogna kunder.</p>\r\n\r\n<a href=\"/?page_id=76\">Mer om införande av affärssystem</a>','Om Gizur','','publish','closed','closed','','om-gizur','','','2012-12-05 10:30:53','2012-12-05 09:30:53','',0,'http://www.gizur.com/?page_id=28',1,'page','',0),(61,1,'2010-10-13 10:58:54','2010-10-13 08:58:54','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/stenbro-300x200.png\" />\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n    <h4>Telefon</h4>\r\n    <p>Jonas Colmsjö, VD</p>\r\n    <p>+46 303 71 91 30</p>\r\n\r\n    <h4>Mail</h4>\r\n    <p>jonas[a]gizur.com</p>\r\n\r\n    <h4>Adress</h4>\r\n    <p>Gizur AB \r\n       Box 84 \r\n       444 21 Stenungsund \r\n       SWEDEN</p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Kontakt','','publish','closed','closed','','kontakt','','','2012-12-03 16:57:47','2012-12-03 15:57:47','',0,'http://www.gizur.com/?page_id=61',4,'page','',0),(76,1,'2010-10-13 11:15:31','2010-10-13 09:15:31','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/rund-300x200.jpeg\" />\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n\r\n    <p class=\"lead\">Gizur har mångårig erfarenhet från förändringsprojekt i ett stort antal företag.</p>\r\n\r\n    <p>Vi har erfarenhet av ERP- och CRM implementeringar samt lager- och transportlösningar</p>\r\n\r\n      <p>Vi kan hjälpa er med:</p>\r\n      <ul>\r\n	<li>Behovsanalys</li>\r\n	<li>Upphandling</li>\r\n	<li>Implementering</li>\r\n	<li>Utrullning</li>\r\n	<li>Bemanning av nyckelroller som t.ex. experter och projektledare</li>\r\n      </ul>\r\n\r\n      <p>Vi arbetar både med stora systemleverantörer som SAP och Oracle och även med moln och open source lösningar vilket passar bra för mindre och medelstora företag.</p>\r\n\r\n<p> Kontakta oss se berättar vi mer.</p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Affärssystem','','publish','closed','closed','','inforande-av-affarssystem','','','2013-02-24 18:31:31','2013-02-24 17:31:31','',268,'http://www.gizur.com/?page_id=76',1,'page','',0),(84,1,'2010-10-13 11:16:29','2010-10-13 09:16:29','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/Gizur-spetsig-bro-300x200.png\" />\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n\r\n\r\n    <p class=\"lead\">Gizur har lång erfarenhet av att stötta företagsledningar i arbetet med strukturera och styra IT. När IT-strategin är definierad och förbättringsprojekt startats är bra projekt- och programledning avgörande för lyckade förändringar.</p>\r\n\r\n  <p>För att säkerställa att IT ger det som som verksamheten behöver krävs ett tätt samarbete mellan IT och verksamheten. Det behövs också en genomtänk strategi för att säkerställa att IT kan utvecklas i samma takt som verksamheten på ett kostnadseffektivt sätt.</p>\r\n\r\nNågra exempel på områden som vi ser som viktiga i en IT-strategi:\r\n<ul>\r\n  <li>Användning av standardsystem eller egenutveckling</li>\r\n  <li>Best-of-breed vs. integrerade system</li>\r\n  <li>Användning av leading-edge teknologier vs. mogna lösningar</li>\r\n  <li>Organisation of utveckling, first-, second line samt grad av offshoring</li>\r\n  <li>En god förståelse för företagets processer är också centralt</li>\r\n</ul>\r\n\r\n\r\n <p>Vi har också den erfarenhet som krävs för att hjälpa våra kunder i verksamhetsutvecklings-projekt. Struktur och metodik är viktiga element för att vara framgångsrik i omfattande projekt och program. Vi utgår ifrån <em>Project Management Institute (PMI)</em> metodik och gör anpassningar till respektive kund. Projektledarna är också certifierade <em>Project Management Professionals (PM)</em> av PMI.</p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','IT Strategi och Projektledning','','publish','closed','closed','','projekt-och-programledning','','','2013-02-22 12:11:52','2013-02-22 11:11:52','',268,'http://www.gizur.com/?page_id=84',4,'page','',0),(90,1,'2010-10-13 11:20:41','2010-10-13 09:20:41','<div class=\"row-fluid\">\r\n  <div class=\"span5\">\r\n\r\n    <img src=\"/wp-content/uploads/img/Gizur-bro-kvall-315x315.png\" />\r\n  \r\n  </div> \r\n  <div class=\"span7\">\r\n\r\n    <p class=\"lead\">Vi utvecklar skräddarsydda web- och mobila lösningar. Genom att använda standardiserade back-end moduler garanteras snabb och effektiv leverans.</p>\r\n\r\n    <p>Inom området mobilitet utvecklar vi för såväl iPhone som Android. Vi utvecklar också web applikationer som fungerar lika bra på,      \r\n       mobila browsers som stora format.</p>\r\n\r\n    <p>Vi lägger stor vikt vid att lösningen ska fungera i verksamheten. Genom en gedigen erfarenheten kan vi ofta hjälpa till att \r\n       förbättra hela processen, inte bara IT-stödet.</p>\r\n     \r\n     <p>Vi har följande standardmoduler som ofta används för att ge projekten en flygande start:<p>\r\n     <ul>\r\n	  <li>Ärendehantering</li>\r\n	  <li>Offerter och kundorder</li>\r\n	  <li>Inköpsorder och lager</li>\r\n	  <li>Fakturor</li>\r\n	  <li>Kontakter, kunder och leverantörer</li>\r\n	  <li>Säljstöd</li>\r\n	  <li>Inventarier</li>\r\n	  <li>Projektstöd</li>\r\n      </ul>\r\n\r\n      <p>Kontakta oss så hjälper vi er att göra en behovsanalys och ta fram förslag på vilka lösningar som skulle kunna passa just er verksamhet.<p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Web och mobilitet','','publish','closed','open','','web-och-mobilitet','','','2013-02-27 09:46:35','2013-02-27 08:46:35','',268,'http://www.gizur.com/?page_id=90',3,'page','',0),(197,1,'2011-11-07 17:58:12','2011-11-07 16:58:12','Vi arbetar inom följande områden:\n\n<!--more-->\n\n1. <a href=\"/?page_id=207\">Detaljhandel</a>\n\n<!--more-->\n\n2. <a href=\"/?page_id=210\">Transport</a>\n\n<!--more-->\n\n3. <a href=\"/?page_id=214\">Fler branscher</a>\n','Branschkunskap','','publish','open','closed','','branschkunskap','','','2013-02-22 11:41:45','2013-02-22 10:41:45','',0,'https://s2.gizur.com/dev-gizur-com/?page_id=197',3,'page','',0),(207,1,'2011-11-07 18:10:09','2011-11-07 17:10:09','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/Knappar-297x198.png\" />\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n\r\n    <p class=\"lead\">Djup kunskap om detaljhandeln. Vi på Gizur har lång erfarenhet av IT inom detaljhandeln och förstår vikten av att sätta verksamheten i fokus. Kunskapen om detaljhandeln i kombination med gediget IT-kunnande är nyckeln till effektiva och kostnadsbesparande lösningar.</p>\r\n\r\n    <h4>Branschkunskap</h4>\r\n\r\n    <p>IT-arbetet idag inom detaljhandeln påverkas av faktorer som t ex vikande efterfrågan, konjunkturosäkerhet och allt kortare produktlivscykler. Dessutom tillkommer ökande krav från kunderna kring områden som t ex rättvis handel. Vi har förståelse för hur man hanterar dessa frågor ur IT-perspektiv.</p>\r\n\r\n    <h4>Exempel på företag vi arbetat med</h4>\r\n    <p>Coop, KF</p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Detaljhandeln','','publish','closed','open','','detaljhandeln','','','2012-12-03 16:49:15','2012-12-03 15:49:15','',197,'https://s2.gizur.com/dev-gizur-com/?page_id=207',0,'page','',0),(210,1,'2011-11-07 18:12:40','2011-11-07 17:12:40','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/Transp-283x295.png\"/>\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n\r\n    <p class=\"lead\">Lång erfarenhet av transport- och logistikbranschen</p>\r\n\r\n    <p class=\"lead\">Vi på Gizur har lång erfarenhet av IT inom transportbranschen och förstår vikten av att sätta verksamheten i fokus. Kunskpen om transporter i kombintaion med gediget IT-kunnande är nyckeln till effektiva och kostnadsbesparande lösningar.</p>\r\n\r\n\r\n    <h4>Branschkunskap</h4>\r\n\r\n    <p>IT-arbetet idag inom transportbranschen påverkas av faktorer som ökande konkurrens, större krav på visibilitet, osäkerhet kring konjunkturen, volatila och höga bränslekostnader samt gröna transporter. Vi har förståelse för hur man hanterar dessa frågor ur IT-perspektiv.</p>\r\n\r\n    <h4>Exempel på företag vi arbetat med</h4>\r\n\r\n    <p>Coop, KF</p>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Transportbranschen','','publish','closed','open','','transportbranschen','','','2012-12-03 16:51:42','2012-12-03 15:51:42','',197,'https://s2.gizur.com/dev-gizur-com/?page_id=210',0,'page','',0),(214,1,'2011-11-07 18:14:35','2011-11-07 17:14:35','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/Gizur-droppekarta-283x354.png\" />\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\">\r\n\r\n    <p class=\"lead\">Vi har tillsammans med våra partners erfarenhet från ett stort antal branscher. </p>\r\n\r\n    <p>Några exempel:</p>\r\n\r\n    <ul>\r\n      <li>Fordonsindustrin</li>\r\n      <li>Läkemedelsindustrin</li>\r\n      <li>Kemiindustrin</li>\r\n      <li>Elektronikindustrin</li>\r\n      <li>Jordbruk</li<\r\n    </ul>\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Övriga branscher','','publish','closed','open','','ovriga-branscher','','','2012-12-03 16:54:26','2012-12-03 15:54:26','',197,'https://s2.gizur.com/dev-gizur-com/?page_id=214',0,'page','',0),(261,1,'2011-11-13 17:12:25','2011-11-13 16:12:25','<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\" style=\"margin-top:0;\">\r\n    <img src=\"/wp-content/uploads/img/Gizur-bro-315x211.png\" >\r\n  </div> <!-- ./span5 -->\r\n\r\n  <div class=\"span7\" style=\"margin-top:0;\">\r\n    <p class=\"lead\">Genom vår partner, Eastern Software System i Indien, kan vi leverera projekt med hög kvalitet och kostnadseffektivt. Kravställning och projekt görs alltid på plats hos kunden. Vi kan också hosta och supportar också lösningarna vi tar fram</p>\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->\r\n<div class=\"row-fluid\">\r\n\r\n  <div class=\"span5\">\r\n    <img src=\"/wp-content/uploads/img/ess_logo.jpg\" >\r\n  </div> <!-- ./span5 -->\r\n  <div class=\"span7\">\r\n    <p>Vi arbetar alltid iterativt med s.k. agila metoder. Ett projekt består av ett antal releaser, även kallade sprints. Ett projekt skulle kunna se ut ungefär så här:</p>\r\n    <ul>\r\n	<li>En projektplan tas fram tillsammans med kunden. Tidplanen innehåller i detta exempel två sprintar</li>\r\n	<li>Kravställning för sprint 1 görs på plats hos kund</li>\r\n	<li>Designen för sprint 1 görs och stäms av med kunden</li>\r\n	<li>Sprint 1 utvecklas</li>\r\n	<li>Sprint 1 testas av kunden</li>\r\n	<li>Ev. fel rättas</li>\r\n	<li>Ev. förändringar som kunden vill göra tas med i sprint 2</li>\r\n	<li>Kravställning för sprint 2 görs på plats hos kund</li>\r\n	<li>Designen för sprint 2 görs och stäms av</li>\r\n	<li>Sprint 2 utvecklas</li>\r\n	<li>Sprint 2 testas av kunden</li>\r\n	<li>Ev. fel rättas </li>\r\n	<li>Utbildning av slutanvändare</li>\r\n	<li>Lösningen tas i bruk</li>\r\n    </ul>\r\n\r\n\r\n  </div><!-- ./span7 -->\r\n</div><!-- ./row-fluid -->','Leveransmodell','','publish','closed','open','','ess-tjanster','','','2013-02-24 18:42:00','2013-02-24 17:42:00','',268,'https://s2.gizur.com/dev-gizur-com/?page_id=261',2,'page','',0),(268,1,'2010-05-28 10:34:31','2010-05-28 08:34:31','Vi arbetar inom följande områden:\n\n<!--more-->\n\n1. <a href=\"/?page_id=90\">Web och mobilitet</a>\n\n<!--more-->\n\n2. <a href=\"/?page_id=76\">Införande av affärssystem</a>\n\n<!--more-->\n\n3. <a href=\"/?page_id=84\">Projektledning</a>\n\n<!--more-->\n\n4. <a href=\"/?page_id=261\">Offshore tjänster</a>','Tjänster','','publish','closed','closed','','tjanster','','','2010-05-28 10:34:31','2010-05-28 08:34:31','',0,'http://www.gizur.com?step=2/?page_id=6',2,'page','',0),(269,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Bra projekt- och programledning är avgörande för lyckade förändringar. Vi har den erfarenhet som krävs för att hjälpa kunderna i dessa ofta utmanade situationer. Struktur och metodik Struktur och metodik som viktiga element för att vara framgångsrik i omfattande projekt och program. Vi utgår ifrån Project Management Institute (PMI) metodik och gör anpassningar till respektive kund. Projektledarna är också certifierade Project Management Professionals (PM) av PMI.','','','publish','open','open','','269','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',268,'http://localhost2/?p=269',3,'nav_menu_item','',0),(270,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Gizur har mångårig erfarenhet från förändringsprojekt i stora och medelstora företag.         Vi arbetar med: Behovsanalys Upphandling Implementering Utrullning Bemanning av nyckelroller som t.ex. experter och projektledare Tillsammans med Groupsoft erbjuder vi kompetens på alla SAP Retail produkter. Exempel på SAP produkter vi har erfarenhet av är: SAP ERP for Retail SAP Extended Warehouse Management SAP Forecasting & Replenishment SAP POS och POS Data Management SAP Business Intelligence SAP Merchandize & Assortment Planning SAP Demand Management Vi arbetar också med Oracles produkter för detaljhandel (f.d Retek). Exempel på produkter vi arbetar med är: Oracle Retail Merchandising System Oracle Retail Price Managemet Oracle Retail Allocations Oracle Retail Invoice Matching Oracle Retail Trade Management Oracle Retail Sales Audit Oracle Retail Store Inventory Management Oracle Retail Warehouse Management Oracle Retail Data Warehouse Oracle Retail Merchandize Financial Planning Oracle Retail Advanced Inventory Planning Oracle Retail Demand Forecasting','','','publish','open','open','','270','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',268,'http://localhost2/?p=270',4,'nav_menu_item','',0),(271,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','','English','','publish','open','open','','english','','','2013-02-24 20:16:34','2013-02-24 19:16:34','',0,'http://localhost2/?p=271',13,'nav_menu_item','',0),(272,1,'2012-11-27 14:23:47','2012-11-27 14:23:47',' ','','','publish','open','open','','272','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',0,'http://localhost2/?p=272',8,'nav_menu_item','',0),(273,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Vi har tillsammans med våra partners erfarenhet från ett stort antal branscher. Några exempel: – Fordonsindustrin – Läkemedelsindustrin – Kemiindustrin – Elektronikindustrin – Jordbruk','Fler branscher','','publish','open','open','','fler-branscher','','','2013-02-24 20:16:34','2013-02-24 19:16:34','',197,'http://localhost2/?p=273',11,'nav_menu_item','',0),(274,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Lång erfarenhet av transport- och logistikbranschen Vi på Gizur har lång erfarenhet av IT inom transportbranschen och förstår vikten av att sätta verksamheten i fokus. Kunskpen om transporter i kombintaion med gediget IT-kunnande är nyckeln till effektiva och kostnadsbesparande lösningar. Branschkunskap IT-arbetet idag inom transportbranschen påverkas av faktorer som ökande konkurrens, större krav på visibilitet, osäkerhet kring konjunkturen, volatila och höga bränslekostnader samt gröna transporter. Vi har förståelse för hur man hanterar dessa frågor ur IT-perspektiv. Exempel på företag vi arbetat med Coop, KF    ','','','publish','open','open','','274','','','2013-02-24 20:16:34','2013-02-24 19:16:34','',197,'http://localhost2/?p=274',10,'nav_menu_item','',0),(275,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Djup kunskap om detaljhandeln Vi på Gizur har lång erfarenhet av IT inom detaljhandeln och förstår vikten av att sätta verksamheten i fokus. Kunskapen om detaljhandeln i kombination med gediget IT-kunnande är nyckeln till effektiva och kostnadsbesparande lösningar. Branschkunskap IT-arbetet idag inom detaljhandeln påverkas av faktorer som t ex vikande efterfrågan, konjunkturosäkerhet och allt kortare produktlivscykler. Dessutom tillkommer ökande krav från kunderna kring områden som t ex rättvis handel. Vi har förståelse för hur man hanterar dessa frågor ur IT-perspektiv. Exempel på företag vi arbetat med Coop, KF','','','publish','open','open','','275','','','2013-02-24 20:16:34','2013-02-24 19:16:34','',197,'http://localhost2/?p=275',9,'nav_menu_item','',0),(276,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Vi arbetar inom tre områden: 1. Införande av affärssystem 2. Projekt- och programledning 3. Open source lösningar','','','publish','open','open','','276','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',0,'http://localhost2/?p=276',2,'nav_menu_item','',0),(277,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Telefon Jonas Colmsjö, VD +46 303 71 91 30 Mail info@gizur.com Adress Gizur Consulting AB Box 84 444 21 Stenungsund SWEDEN','','','publish','open','open','','277','','','2013-02-24 20:16:34','2013-02-24 19:16:34','',0,'http://localhost2/?p=277',12,'nav_menu_item','',0),(278,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','Genom vår partner, Eastern Software System i Indien, erbjuder vi kompetens inom följande områden:             Utveckling i Java. Microsoft.NET utveckling och Sharepoint lösningar. Databasutveckling (Oracle, Microsoft, MySQL och PostgreSQL. E-handelslösningar. Web 2.0 portaler och sociala medier. Dokumenthanteringssystem. Systemintregration. Molnlösningar. Business Intelligence – Oracle Discovey, Microsoft och en egenutvecklad BI-lösning. Outsourcing av databas- och infrastruktureradministration.','','','publish','open','open','','278','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',268,'http://localhost2/?p=278',7,'nav_menu_item','',0),(279,1,'2012-11-27 14:23:47','2012-11-27 14:23:47','  Lösningar baserad på öppen källkod, open source, blir allt vanligare. Open source lösningar finns inom en mängd områden, även om operativsystemet Linux är det mest kända exemplet. Viktiga fördelar, förutom en låg kostnad, är att det bildas sk. communities där det finns möjlighet att få hjälp och support.       Vi arbetar med följande open source lösningar: ERP lösningar – OpenERP CRM lösningar – vTigerCRM och OpenERP Master Data Management – Talend Business Intelligence – SpagoBI (analys) och Talend (ETL) Lösningar för dokumentation – KnowledgeTree Behovsanalys Vi hjälper er att göre en behovsanalys och ta fram förslag på vilka lösningar som skulle kunna passa er verksamhet bäst.','','','publish','open','open','','279','','','2013-02-24 20:16:33','2013-02-24 19:16:33','',268,'http://localhost2/?p=279',5,'nav_menu_item','',0),(281,1,'2010-10-13 11:20:41','2010-10-13 09:20:41','<h2></h2>\n<h2><img class=\"alignleft  wp-image-251\" title=\"Gizur. bro kväll\" src=\"/wp-content/uploads/2012/11/Gizur-bro-kväll-315x315.png\" alt=\"\" width=\"315\" height=\"315\" /></h2>\n<strong>Vi utvecklar weblösningar baserade på  öppen källkod, open source, vilket blir allt vanligare. Open source lösningar finns inom en mängd områden, även om operativsystemet Linux är det mest kända exemplet.</strong>\n\n<span style=\"font-weight: normal;\">Viktiga fördelar, förutom en låg kostnad, är att det bildas sk. communities där det finns möjlighet att få hjälp och support.</span>\n\nInom området mobilitet utvecklar vi för såväl iPhone som Android. Vi utvecklar också  s.k. responsive web apps, dvs. web lösningar som fungerar i både mobila browsers och stora format.\n\n<br style=\"clear:both;\"/> \n\n<strong>Några exempel på tekniker som vi arbetar med:</strong>\n<ul>\n<ul>\n	<li>CRM lösningar baserad på vTiger</li>\n<ul>\n	<li>Hantering av leads och kontakter</li>\n	<li>Offerter</li>\n	<li>Kund- och leverantörsorder</li>\n	<li>Ärendehantenring</li>\n</ul>\n	<li>PHP och MySQL</li>\n	<li>HTML5/JavaScript - Att utveckla hela applikationer i HTML5/JavaScript börjar bli populärt</li>\n	<li>NodeJS - Utveckling i NodeJS, dvs. server side JavaScript, blir allt vanligare</li>\n	<li>NoSQL databaser - CouchDB, MongoDB och DynamoDB</li>\n	<li>Molnlösningar - Amazon och Heroku</li>\n	<li>Mobila lösningar - appar för både iPhone och Androids samt responsive web apps</li>\n</ul>\n</ul>\n\nKontakta oss så hjälper vi er att göra en behovsanalys och ta fram förslag på vilka lösningar som skulle kunna passa just er verksamhet.','Web och mobilitet','','inherit','open','open','','90-revision','','','2010-10-13 11:20:41','2010-10-13 09:20:41','',90,'http://localhost2/281/90-revision/',0,'revision','',0),(282,1,'2012-12-03 15:07:16','2012-12-03 14:07:16','<h2></h2>\r\n<h2><img class=\"alignleft  wp-image-251\" title=\"Gizur. bro kväll\" src=\"/wp-content/uploads/img/Gizur-bro-kväll-315x315.png\" alt=\"\" width=\"315\" height=\"315\" /></h2>\r\n<strong>Vi utvecklar weblösningar baserade på öppen källkod, open source, vilket blir allt vanligare. Open source lösningar finns inom en mängd områden, även om operativsystemet Linux är det mest kända exemplet.</strong>\r\n\r\n<span style=\"font-weight: normal;\">Viktiga fördelar, förutom en låg kostnad, är att det bildas sk. communities där det finns möjlighet att få hjälp och support.</span>\r\n\r\nInom området mobilitet utvecklar vi för såväl iPhone som Android. Vi utvecklar också s.k. responsive web apps, dvs. web lösningar som fungerar i både mobila browsers och stora format.\r\n\r\n&nbsp;\r\n\r\n<strong>Några exempel på tekniker som vi arbetar med:</strong>\r\n<ul>\r\n<ul>\r\n	<li>CRM lösningar baserad på vTiger\r\n<ul>\r\n	<li>Hantering av leads och kontakter</li>\r\n	<li>Offerter</li>\r\n	<li>Kund- och leverantörsorder</li>\r\n	<li>Ärendehantenring</li>\r\n</ul>\r\n</li>\r\n	<li>PHP och MySQL</li>\r\n	<li>HTML5/JavaScript - Att utveckla hela applikationer i HTML5/JavaScript börjar bli populärt</li>\r\n	<li>NodeJS - Utveckling i NodeJS, dvs. server side JavaScript, blir allt vanligare</li>\r\n	<li>NoSQL databaser - CouchDB, MongoDB och DynamoDB</li>\r\n	<li>Molnlösningar - Amazon och Heroku</li>\r\n	<li>Mobila lösningar - appar för både iPhone och Androids samt responsive web apps</li>\r\n</ul>\r\n</ul>\r\nKontakta oss så hjälper vi er att göra en behovsanalys och ta fram förslag på vilka lösningar som skulle kunna passa just er verksamhet.','Web och mobilitet','','inherit','open','open','','90-revision-2','','','2012-12-03 15:07:16','2012-12-03 14:07:16','',90,'http://localhost2/282/90-revision-2/',0,'revision','',0),(283,1,'2012-12-03 15:09:16','2012-12-03 14:09:16','<h2></h2>\r\n<h2><img class=\"alignleft  wp-image-251\" title=\"Gizur. bro kväll\" src=\"/wp-content/img/Gizur-bro-kvall-315x315.png\" alt=\"\" width=\"315\" height=\"315\" /></h2>\r\n<strong>Vi utvecklar weblösningar baserade på öppen källkod, open source, vilket blir allt vanligare. Open source lösningar finns inom en mängd områden, även om operativsystemet Linux är det mest kända exemplet.</strong>\r\n\r\n<span style=\"font-weight: normal;\">Viktiga fördelar, förutom en låg kostnad, är att det bildas sk. communities där det finns möjlighet att få hjälp och support.</span>\r\n\r\nInom området mobilitet utvecklar vi för såväl iPhone som Android. Vi utvecklar också s.k. responsive web apps, dvs. web lösningar som fungerar i både mobila browsers och stora format.\r\n\r\n&nbsp;\r\n\r\n<strong>Några exempel på tekniker som vi arbetar med:</strong>\r\n<ul>\r\n<ul>\r\n	<li>CRM lösningar baserad på vTiger\r\n<ul>\r\n	<li>Hantering av leads och kontakter</li>\r\n	<li>Offerter</li>\r\n	<li>Kund- och leverantörsorder</li>\r\n	<li>Ärendehantenring</li>\r\n</ul>\r\n</li>\r\n	<li>PHP och MySQL</li>\r\n	<li>HTML5/JavaScript - Att utveckla hela applikationer i HTML5/JavaScript börjar bli populärt</li>\r\n	<li>NodeJS - Utveckling i NodeJS, dvs. server side JavaScript, blir allt vanligare</li>\r\n	<li>NoSQL databaser - CouchDB, MongoDB och DynamoDB</li>\r\n	<li>Molnlösningar - Amazon och Heroku</li>\r\n	<li>Mobila lösningar - appar för både iPhone och Androids samt responsive web apps</li>\r\n</ul>\r\n</ul>\r\nKontakta oss så hj